<?phprequire 'config.php';require '../Slim/Slim.php';require('../libraries-3scale/ThreeScaleClient.php');\Slim\Slim::registerAutoloader();$app = new \Slim\Slim();// root$route = '/';$app->get($route, function ()  use ($app){    	$E = array();												$E['Message'] = "Sorry, you do not have access!";		$app->response()->status(403);		$app->response()->header("Content-Type", "application/json");	echo format_json(json_encode($E));						});		include "methods/business.php";$app->run();?>